import React from "react";
import bar from '../src/images/bar.png';
import gal1 from '../src/images/gal1.png';
import gal2 from '../src/images/gal2.png';
import gal3 from '../src/images/gal3.png';
import gal4 from '../src/images/gal4.png';
import gal5 from '../src/images/gal5.png';
import gal6 from '../src/images/gal6.png';
import gal7 from '../src/images/gal7.png';
import gal8 from '../src/images/gal8.png';
import AddIcon from '@material-ui/icons/Add';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';

const GallarySection =()=>{
   // Add active class to the current button (highlight it)
var btnContainer = document.getElementById("myBtnContainer");
if (btnContainer !== null) {
    var btns = btnContainer.getElementsByClassName("btn1");
    for (var i = 0; i < btns.length; i++) {
        btns[i].addEventListener("click", function(){
          var current = document.getElementsByClassName("active");
          current[0].className = current[0].className.replace(" active", "");
          this.className += " active";
        });
      }
}

    filterSelection("all");
function filterSelection(c) {
  var x, i;
  x = document.getElementsByClassName("column");
  if (c == "all") c = "";
  for (i = 0; i < x.length; i++) {
    w3RemoveClass(x[i], "show");
    if (x[i].className.indexOf(c) > -1) w3AddClass(x[i], "show");
  }
}

function w3AddClass(element, name) {
  var i, arr1, arr2;
  arr1 = element.className.split(" ");
  arr2 = name.split(" ");
  for (i = 0; i < arr2.length; i++) {
    if (arr1.indexOf(arr2[i]) == -1) {element.className += " " + arr2[i];}
  }
}

function w3RemoveClass(element, name) {
  var i, arr1, arr2;
  arr1 = element.className.split(" ");
  arr2 = name.split(" ");
  for (i = 0; i < arr2.length; i++) {
    while (arr1.indexOf(arr2[i]) > -1) {
      arr1.splice(arr1.indexOf(arr2[i]), 1);     
    }
  }
  element.className = arr1.join(" ");
}




    return(
    <>
<section className="gallary-sec">
<div className="container-fluid">
<div className="row bg-transparent ">
                <div className="col-8 mx-auto top-gallary-text text-center">
                    <h3>FEATURED PRODUCTS</h3>
                    <img src={bar} alt="bar" />
                    <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit sed do eiusmod 
tempor incididunt ut labore et dolore magnam </p>
            
                </div>
            </div>
            <div className="row">
<div id="myBtnContainer" className="col-8 mx-auto text-center p-4" >
  <button className="btn1 mx-lg-2 active" onclick="filterSelection('all')"> Show all</button>
  <button className="btn1 mx-lg-2" onclick="filterSelection('design')"> Design</button>
  <button className="btn1 mx-lg-2" onclick="filterSelection('code')"> Code</button>
  <button className="btn1 mx-lg-2" onclick="filterSelection('photography')"> Photography</button>
</div>
</div>
<div className="row">
  <div className="column design col-lg-4 col-md-6 col-12 p-0">
    <div className="content">
      <img src={gal1} alt="Mountains" />
      <a href="#"  title="User Profile">
      <div class="overlay">
 
  <span>
  <AddIcon className="icon"/>
  </span>

  </div>
  </a>

    </div>
  </div>
  <div className="column design col-lg-4 col-md-6 col-12 p-0">
    <div className="content">
    <img src={gal2}  alt="Lights" />
